import React from 'react'
import { Form, Input, Message } from 'semantic-ui-react'

export default class Quantity extends React.Component {

    state = {
        qtyTerm: ""
    }

    handleOnChange = (evt) => {
        this.setState({qtyTerm: evt.target.value})

    }

    render() {

        return (
            <Form  onSubmit={() => {this.props.addQty(evt)}} size='massive'>
                    <Form.Field inline>
                        <label>How Many People Are Eating?</label>
                        <Input placeholder='1+' value={this.state.qtyTerm} onChange = {this.handleOnChange} />
                        <Message success header='Got It!'/>
                    </Form.Field>
                </Form>

        )
    }

}
