import React from 'react'
import { useBooleanKnob } from '@stardust-ui/docs-components/commonjs'
import { Image, Divider, Dropdown, Menu, Sidebar, Segment, Icon } from 'semantic-ui-react'
import iunnologo from '../Assets/iunnologo.png'
import { NavLink } from 'react-router-dom'

export default class Header extends React.Component {

    state = {}

  handleItemClick = (e, { name }) => this.setState({ activeItem: name })



    render() {
        const { activeItem } = this.state
        const [visible, setVisible] = useBooleanKnob({ name: 'visible' })

        return (


        <Menu stackable >
        <Menu.Item>
          <Image size='mini' src={iunnologo} />
        </Menu.Item>

       <Menu.Item
          name='history'
          active={activeItem === 'history'}
          onClick={this.handleItemClick}
        >
<Sidebar.Pushable as={Segment}>
      <Sidebar
        as={Menu}
        animation='overlay'
        icon='labeled'
        inverted
        onHide={() => setVisible(false)}
        vertical
        visible={visible}
        width='thin'
      >
        <Menu.Item as='a'>
          <Icon name='home' />
          Home
        </Menu.Item>
        <Menu.Item as='a'>
          <Icon name='gamepad' />
          Games
        </Menu.Item>
        <Menu.Item as='a'>
          <Icon name='camera' />
          Channels
        </Menu.Item>
      </Sidebar>

      <Sidebar.Pusher dimmed={visible}>
        <Segment basic>
          <Header as='h3'>Application Content</Header>
          <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
        </Segment>
      </Sidebar.Pusher>
    </Sidebar.Pushable>

        </Menu.Item>

        <NavLink to='/profile'> <Menu.Item
          name='profile'
          active={activeItem === 'profile'}
          onClick={this.handleItemClick}
        >
          Profile
        </Menu.Item></NavLink>

        <Menu.Item
          name='sign-out'
          active={activeItem === 'sign-out'}
          onClick={() => this.props.signOut()}
        >
          Sign-out
        </Menu.Item>
      </Menu>
        //     <>
        //         <center>
        //             <Divider hidden/>
        //             <NavLink to='/'><Image src={iunnologo} /></NavLink>
        //             <Divider hidden/>
        //             {/* <Divider hidden/> */}

        //             <Dropdown basic icon='align justify' floating   className='icon'>
        //                 <Dropdown.Menu>
        //                     <Dropdown.Item text='Cancel Order' />
        //                     <NavLink to='/history'> <Dropdown.Item text='View Order History' /> </NavLink>
        //                     <NavLink to='/profile'> <Dropdown.Item text='Edit Profile'/> </NavLink>
        //                     <NavLink to='/'> <Dropdown.Item onClick={() => this.props.signOut()} text='Sign Out'/> </NavLink>
        //                 </Dropdown.Menu>
        //             </Dropdown>
        //         </center>


        // </>

        )
    }

}
