import React from 'react'
import { Grid, Divider} from 'semantic-ui-react'
import UserOrder from './UserOrder'

export default class History extends React.Component {

    renderUserOrders = () => { 

        fetch('http://localhost:3000/api/v1/meals')
      .then(res => res.json())
      .then(data => {
        this.setState({
          allMeals: data
        });
      })

        return this.props.user.orders.map(order => {
        return  <UserOrder order={order} />
            }) 
        }      

    render() {
        console.log(this.props.user.orders)

        return (
            <>
                <center>
                    <Divider hidden/>
                    <h1>{this.props.user.first_name}'s Order History</h1>
                    <Grid columns='equal'>
                        <Grid.Row columns={4}>
                        {this.renderUserOrders()}
                        </Grid.Row>
                    </Grid>
                </center>


        </>

        )
    }

}
