import React from 'react'
import Homepage from './Homepage'
import Forms from './Typewriter';
// import dinner from './dinner.jpg'

export default class Content extends React.Component {

    renderContent = () => {
        if (this.props.logged === true) {
          return <Homepage key={'homepage'}  allMeals={this.props.allMeals} chooseMeal={this.props.chooseMeal} randomMeal={this.props.randomMeal} sessionUser={this.props.sessionUser}/>;
        } else {
          return <Forms key={'forms'} logIn={this.props.logIn} handleRegisterSubmit={this.props.handleRegisterSubmit} handleLoginSubmit={this.props.handleLoginSubmit}/>;
        }
    }

    render() {
        return (
            <div>
            {this.renderContent()}
            </div>
        )
    }
}
