import React from 'react'
import { Image, Divider, Dropdown, Menu } from 'semantic-ui-react'
import iunnologo from '../Assets/iunnologo.png'
import iunnologosmall from '../Assets/iunnologosmall.png'

import { NavLink } from 'react-router-dom'

export default class Header extends React.Component {

    state = {}

    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render() {

        const { activeItem } = this.state

        return (


            <Menu fixed="top" inverted>
        <Menu.Item>
          <Image size='small' src={iunnologo} />
        </Menu.Item>

        <NavLink to='/history'><Menu.Item
          name='history'
          active={activeItem === 'history'}
          onClick={this.handleItemClick}
        >
          Order History
        </Menu.Item></NavLink>

        <NavLink to='/profile'> <Menu.Item
          name='profile'
          active={activeItem === 'profile'}
          onClick={this.handleItemClick}
        >
          Profile
        </Menu.Item></NavLink>

        <Menu.Item
          name='sign-out'
          active={activeItem === 'sign-out'}
          onClick={this.handleItemClick}
        >
          Sign-out
        </Menu.Item>
      </Menu>
