import React from 'react';
import './App.css';
// import Content from './Components/Content.jsx'
import Header from './Components/Header.jsx'
import {Switch, Route, Redirect} from 'react-router'
import {withRouter} from 'react-router-dom'
import Forms from './Components/Forms.jsx'
import Welcome from './Components/Welcome'
import Homepage from './Components/Homepage';
// import ProfileContainer from './ProfileContainer'

// import dinner from './dinner.jpg'

class App extends React.Component {

  state = {
    user: {
      // email: "",
      // id: 0
    },
    token: "",
    allMeals: [],
    randomMeal: [],
    sessionOrder: []

  }
  chooseMeal = () => {
    this.setState({ randomMeal: this.state.allMeals[Math.floor(Math.random()*this.state.allMeals.length)] }) 
  }

  componentDidMount(){
    fetch('http://localhost:3000/api/v1/meals')
      .then(res => res.json())
      .then(data => console.log(data));
}


    renderContent = () => {
        if (this.props.user) {
          return <Homepage key={'homepage'} chooseMeal={this.chooseMeal} randomMeal={this.props.randomMeal} sessionUser={this.props.sessionUser}/>;
        } else {
          return this.props.renderForms
        }
    }


  // componentDidMount() {
  //   if (localStorage.getItem("token")) {
  //     let token = localStorage.getItem("token")

  //     fetch("http://localhost:3000/persist", {
  //       headers: {
  //         "Authorization": `bearer ${token}`
  //       }
  //     })
  //     .then(r => r.json())
  //     .then((data) => {
  //       if (data.token) {
  //         localStorage.setItem("token", data.token)
  //         this.setState({
  //           user: data.user,
  //           token: data.token
  //         }, () => {
  //           this.props.history.push("/profile")
  //         })
  //       }
  //     })
  //   }
  // }



  handleLoginSubmit = (userInfo) => {
    console.log("Login form has been submitted")
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data);
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/homepage")
        })

      }
    })

  }

  handleRegisterSubmit = (userInfo) => {
    // debugger
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data)
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/homepage")
        })
      }
    })
  }

  renderForms = (routerProps) => {
    if(routerProps.location.pathname === "/login"){
      return <Forms formName="Login Form" log={this.log} reg={this.reg} handleSubmit={this.handleLoginSubmit}/>
    } else if (routerProps.location.pathname === "/register") {
      return <Forms formName="Register Form" log={this.log} reg={this.reg} handleSubmit={this.handleRegisterSubmit}/>
    }
  }

  // renderProfile = (routerProps) => {
  //   return <ProfileContainer token={this.state.token} user={this.state.user} />
  // }

  render(){
    console.log(this.state, "APP STATE")
    console.log(this.props, "APP PROPS")

    return (
      <div className="App">
        <Header/>
        <Switch>
          <Route path="/login" render={this.renderForms} />
          <Route path="/register" render={this.renderForms} />
          <Route path="/profile" render={ this.renderProfile } />
          <Route path="/homepage" render={() => <Homepage/> } />
          <Route path="/" exact render={() => <Welcome/> } />
          <Route render={ () => <p>Page not Found</p> } />
          {/* <Redirect exact from="/" to="login" /> */}
        </Switch>
      </div>
    );
  }

}

export default withRouter(App);
