import React from 'react';
import './App.css';
import MainContainer from './MainContainer'
import Header from './Header'
import {Switch, Route, Router} from 'react-router'
import {withRouter} from 'react-router-dom'

// import dinner from './dinner.jpg'

 class App extends React.Component {

  state = {
    user: {
      email: "",
      id: 0
    },
    token: ""
  }

  componentDidMount() {
    if (localStorage.getItem("token")) {
      let token = localStorage.getItem("token")

      fetch("http://localhost:3000/persist", {
        headers: {
          "Authorization": `bearer ${token}`
        }
      })
      .then(r => r.json())
      .then((data) => {
        if (data.token) {
          localStorage.setItem("token", data.token)
          this.setState({
            user: data.user,
            token: data.token
          }, () => {
            this.props.history.push("/profile")
          })
        }
      })


    }
  }

  // addOneSnack = (snackObj) => {
  //   let modifiedArray = [...this.state.user.snacks, snackObj]
  //   this.setState({
  //     user: {
  //       ...this.state.user,
  //       snacks: modifiedArray
  //     }
  //   })
  // }

  handleLoginSubmit = (userInfo) => {
    console.log("Login form has been submitted")
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data);
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/profile")
        })

      }
    })

  }

  handleRegisterSubmit = (userInfo) => {
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    // .then(data => {
    //   if (!data.error) {
    //     localStorage.setItem("token", data.token)
    //     this.setState({
    //       user: data.user,
    //       token: data.token
    //     }, () => {
    //       this.props.history.push("/profile")
    //     })
    //   }
    // })
  }


  render() {
    return (
      <div className="App" >
        <Header/>
        <MainContainer handleRegisterSubmit={this.handleRegisterSubmit} handleLoginSubmit={this.handleLoginSubmit}/>

      </div>
    );
  }

}
export default App

