import React from 'react'
import iunnologo4button from '../Assets/iunnologo4button.png'
import { Image } from 'semantic-ui-react';

export default class OrderButton extends React.Component {

    render() {
        return (
        <div class="bigbutton-wrap">
            <button class="big-button" onClick={()=>{this.props.createOrder(); this.props.increment()}}><Image centered size='medium' src={iunnologo4button}/><span><h1></h1>No turning back from here!!!</span></button>
        </div>
        )
    }

}
