import React from 'react'
import Homepage from './Homepage'
import Forms from './Forms';
// import dinner from './dinner.jpg'

export default class Content extends React.Component {

  state = {
    allMeals: [],
    sessionUser: [],
    randomMeal: [],
    sessionOrder: []

}

renderForms = (routerProps) => {
  if(routerProps.location.pathname === "/login"){
    return <Forms formName="Login Form" handleSubmit={this.handleLoginSubmit}/>
  } else if (routerProps.location.pathname === "/register") {
    return <Forms formName="Register Form" handleSubmit={this.handleRegisterSubmit}/>
  }
}

chooseMeal = () => {
    this.setState({ randomMeal: this.state.allMeals[Math.floor(Math.random()*this.state.allMeals.length)] }) 
  }

logIn = () => {
    this.setState({logged: true})
}

componentDidMount(){
    fetch('http://localhost:3000/api/v1/meals')
      .then(res => res.json())
      .then(data => console.log(data));
}


    renderContent = () => {
        if (this.props.logged === true) {
          return <Homepage key={'homepage'}  allMeals={this.props.allMeals} chooseMeal={this.props.chooseMeal} randomMeal={this.props.randomMeal} sessionUser={this.props.sessionUser}/>;
        } else {
          return <Forms key={'forms'} logIn={this.props.logIn} handleRegisterSubmit={this.props.handleRegisterSubmit} handleLoginSubmit={this.props.handleLoginSubmit}/>;
        }
    }

    render() {
        return (
            <div>
            {this.renderContent()}
            </div>
        )
    }
}
