import React from 'react'
import { Button, Form, Grid, Header, Segment, Divider } from 'semantic-ui-react'

export default class Forms extends React.Component {

  state = {
    email: "",
    password: "",
    firstName: "",
    lastName: ""
  }

  // handleRegSubmit = (e) => {
  //   e.preventDefault()
  //   this.props.handleRegisterSubmit(this.state)
  // }

  // handleLogSubmit = (e) => {
  //   e.preventDefault()
  //   this.props.handleLoginSubmit(this.state)
  // }


  handleChange = (e) => {
    let {name, value} = e.target
    this.setState({
      [name]: value
    })
  }

  render() {
    let {formName} = this.props
    let {email, password} = this.state
    // console.log(this.state.email, this.state.password);

    return(
      <>
      <Divider hidden/>
      <Grid textAlign='center' style={{ height: '1vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as='h2' color='olive' textAlign='center'>
            {formName}
          </Header>
          <Form size='small' onSubmit={() => this.props.handleSubmit(this.state)}>
            <Segment>
              <Form.Input required  fluid icon='user' iconPosition='left' placeholder='E-mail address' name="email" value={email} onChange={this.handleChange}/>
              <Form.Input required  fluid icon='lock' iconPosition='left' placeholder='Password' type='password' name="password" value={password} onChange={this.handleChange}/>
                <Button color='olive'>Submit</Button>
            </Segment>
          </Form>
          {/* <Message>
            New to us? <a href='#'>Sign Up</a>
          </Message> */}
        </Grid.Column>
      </Grid>
      </>
    )
  }
}
