  //       }
  //     })
  //     .then(r => r.json())
  //     .then((data) => {
  //       if (data.token) {
  //         localStorage.setItem("token", data.token)
  //         this.setState({
  //           user: data.user,
  //           token: data.token
  //         }, () => {
  //           this.props.history.push("/profile")
  //         })
  //       }
  //     })
  //   }
  // }



  handleLoginSubmit = (userInfo) => {
    console.log("Login form has been submitted")
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data);
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/homepage")
        })

      }
    })

  }

  handleRegisterSubmit = (userInfo) => {
    // debugger
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log("hey")
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/homepage")
        })
      }
    })
  }

  renderForms = (routerProps) => {
    if(routerProps.location.pathname === "/login"){
      return <Forms formName="Login Form" log={this.log} reg={this.reg} handleSubmit={this.handleLoginSubmit}/>
    } else if (routerProps.location.pathname === "/register") {
      return <Forms formName="Register Form" log={this.log} reg={this.reg} handleSubmit={this.handleRegisterSubmit}/>
    }
  }

  // renderProfile = (routerProps) => {
  //   return <ProfileContainer token={this.state.token} user={this.state.user} />
  // }

  render(){
    console.log(this.state, "APP STATE")
    console.log(this.props, "APP PROPS")

    return (
      <div className="App">
        <Header/>
        {/* <Router> */}
        <Switch>
          <Route path="/login" render={this.renderForms} />
          <Route path="/register" render={this.renderForms} />
          <Route path="/profile" render={ this.renderProfile } />
          <Route path="/homepage" render={ <Homepage/> } />
          <Route path="/" exact render={() => <Welcome/> } />
          <Route render={ () => <p>Page not Found</p> } />
          {/* <Redirect exact from="/" to="login" /> */}
        </Switch>
        {/* </Router> */}
      </div>
    );
  }

}

export default withRouter(App);
