import React from 'react'
import { Form, Input, Message } from 'semantic-ui-react'

export default class Quantity extends React.Component {



    render() {

        return (
            <Form  onSubmit={() => {this.props.addQty()}} size='massive'>
                    <Form.Field inline>
                        <label>How Many People Are Eating?</label>
                        <Input placeholder='1+' value={this.props.qtyTerm} onChange = {this.props.handleOnChange} />
                        <Message success header='Got It!'/>
                    </Form.Field>
                </Form>

        )
    }

}
