import React from 'react'
import iunnologo4button from '../Assets/iunnologo4button.png'
import { Image, Modal, Button } from 'semantic-ui-react';

export default class OrderButton extends React.Component {

    state = {
        open: false
    }

    show = (dimmer) => () => this.setState({ dimmer, open: true })
    close = () => this.setState({ open: false })

    render() {

        const { open, dimmer } = this.state

        return (

            <>
                <div class="bigbutton-wrap">
                    <button class="big-button" onClick={this.show('blurring')}><Image centered size='medium' src={iunnologo4button}/></button>
                </div>

                <Modal size='mini' dimmer={dimmer} open={open} onClose={this.close}>
                    <Modal.Header>Are You Sure?</Modal.Header>
                    <Modal.Content>
                        <p>iunno will choose a random meal for you and charge your card</p>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button onClick={this.close} negative>Too Much Pressure</Button>
                        <Button onClick={()=>{this.props.createOrder(); this.props.increment()}} positive icon='checkmark' labelPosition='right' content='Confirm Order'/>
                    </Modal.Actions>
                </Modal>
            </>

        )
    }

}
