import React from 'react'
import { Divider } from 'semantic-ui-react'
import Quantity from './Quantity.jsx'
import OrderButton from './OrderButton.jsx'
import Confirmation from './Confirmation.jsx'

export default class Homepage extends React.Component {

    state = {
        sessionQty: 0,
        qtyTerm: "",
        orderCreated: false,
        removeButton: false,
        sessionOrder: {}
    }

    addQty = () => {
        this.setState({sessionQty: this.state.qtyTerm })
    }


    handleOnChange = (evt) => {
        this.setState({qtyTerm: evt.target.value})

    }



    createOrder = () => {
      this.props.chooseMeal();

        fetch(`http://localhost:3000/api/v1/orders`, {
            method: 'POST',
            body: JSON.stringify({
              user_id: this.props.user.id, 



            }),
            headers:{
              'Content-Type': 'application/json',
              "Accept": "application/json"
            }
          })
          .then(res => res.json())
          .then(data => this.setState({orderCreated: true, removeButton: true, sessionOrder: data}))  
          .then(this.createOrderMeal())  
    }

    createOrderMeal = () => {
      fetch(`http://localhost:3000/api/v1/order_meals`, {
            method: 'POST',
            body: JSON.stringify({
              // order_id: this.state.sessionOrder.id, 
              // meal_id: this.props.randomMeal.id

              // order_id: 1,
              // meal_id: 2
            }),
            headers:{
              'Content-Type': 'application/json',
              "Accept": "application/json"
            }
          })
          .then(res => res.json())
          .then(data => console.log(data))
    }

    renderOrderContent = () => {
        if (this.state.sessionQty > 0) {
          return <OrderButton key={'orderbutton'} sessionQty={this.state.sessionQty} createOrder={this.createOrder} chooseMeal={this.props.chooseMeal} randomMeal={this.props.randomMeal} />
        } else if (this.state.orderCreated === true ) {
          return <Confirmation key={'confirmtion'} user={this.props.user} randomMeal={this.props.randomMeal}/>
        } else {
          return <Quantity key={'quantity'} user={this.props.user} qtyTerm={this.state.qtyTerm} addQty={this.addQty} handleOnChange={this.handleOnChange}/>;
        }
      }

    render() {
console.log(this.props.randomMeal)
console.log(this.state.sessionOrder)
console.log(this.state.orderCreated)

        return (
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                {this.renderOrderContent()}
          </>

        )
    }

}
