import React from 'react';
import './App.css';
import Content from './Components/Content'
import Header from './Components/Header'
import {Switch, Route, Redirect, Router} from 'react-router'
import {withRouter} from 'react-router-dom'
import Forms from './Components/Forms'
// import ProfileContainer from './ProfileContainer'

// import dinner from './dinner.jpg'

class App extends React.Component {

  state = {
    user: {
      email: "",
      id: 0
    },
    token: ""
  }

  // componentDidMount() {
  //   if (localStorage.getItem("token")) {
  //     let token = localStorage.getItem("token")

  //     fetch("http://localhost:3000/persist", {
  //       headers: {
  //         "Authorization": `bearer ${token}`
  //       }
  //     })
  //     .then(r => r.json())
  //     .then((data) => {
  //       if (data.token) {
  //         localStorage.setItem("token", data.token)
  //         this.setState({
  //           user: data.user,
  //           token: data.token
  //         }, () => {
  //           this.props.history.push("/profile")
  //         })
  //       }
  //     })
  //   }
  // }



  handleLoginSubmit = (userInfo) => {
    console.log("Login form has been submitted")
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data);
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/profile")
        })

      }
    })

  }

  handleRegisterSubmit = (userInfo) => {
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/profile")
        })
      }
    })
  }

  renderForms = (routerProps) => {
    if(routerProps.location.pathname === "/login"){
      return <Forms formName="Login Form" handleSubmit={this.handleLoginSubmit}/>
    } else if (routerProps.location.pathname === "/register") {
      return <Forms formName="Register Form" handleSubmit={this.handleRegisterSubmit}/>
    }
  }

  // renderProfile = (routerProps) => {
  //   return <ProfileContainer token={this.state.token} user={this.state.user} />
  // }

  render(){
    console.log(this.state, "APP STATE")
    console.log(this.props, "APP PROPS")

    return (
      <div className="App">
        <Header/>
        {/* <Router> */}
        <Switch>
          <Route path="/login" render={ <Content key={'content'} user={this.state.user} renderForms={this.renderForms} handleRegisterSubmit={this.handleRegisterSubmit} handleLoginSubmit={this.handleLoginSubmit}/> } /> } />
          <Route path="/register" render={ <Content key={'content'} user={this.state.user} renderForms={this.renderForms} handleRegisterSubmit={this.handleRegisterSubmit} handleLoginSubmit={this.handleLoginSubmit}/> } /> } />
          <Route path="/profile" render={ this.renderProfile } />
          <Route path="/" exact render={() => <Content key={'content'} renderForms={this.renderForms} handleRegisterSubmit={this.handleRegisterSubmit} handleLoginSubmit={this.handleLoginSubmit}/> } />
          <Route render={ () => <p>Page not Found</p> } />
          <Redirect exact from="/" to="login" />
        </Switch>
        {/* </Router> */}
      </div>
    );
  }

}

export default withRouter(App);
