import React from 'react'
import { Form, Input, Divider, Message } from 'semantic-ui-react'
import Quantity from './Quantity'
import OrderButton from './OrderButton'
import Confirmation from './Confirmation'

export default class Homepage extends React.Component {

    state = {
        sessionQty: 0,
        orderCreated: false
    }

    addQty = () => {
        this.setState({sessionQty: 1})
    }

    createOrder = () => {
        this.setState()
    }

    renderOrderContent = () => {
        if (this.state.sessionQty > 0) {
          return <OrderButton/>
        } else if (this.state.orderCreated === true) {
          return <Confirmation/>
        } else {
          return <Quantity key={'quantity'} addQty={this.addQty}/>;
        }
      }

    render() {
        console.log(this.state.sessionQty);

        return (
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                {this.renderOrderContent()}
          </>

        )
    }

}
