import React from 'react'
import { Button, Form, Segment, Grid } from 'semantic-ui-react'


export default class ProfileContainer extends React.Component {

    state = {
        email: "",
        phone: "",
        address: "",
        cityTown: "",
        state: "",
        zipCode: "",
        ccNum: "",
        expDate: "",
        securityCode: "",
        billingZip: "",
        open: false
      }

    show = (dimmer) => () => this.setState({ dimmer, open: true })
    close = () => this.setState({ open: false })

      handleChange = (e) => {
        let {name, value} = e.target
        this.setState({
          [name]: value
        })
      }

      handleProfileSubmit = (userInfo) => {
        console.log("Profile form has been submitted")
        fetch("http://localhost:3000/api/v1/users", {
          method: "PATCH",
          headers: {
            "content-type": "application/json"
          },
          body: JSON.stringify(
            userInfo
          )
        })
        .then(r => r.json())
        .then(data => {
          console.log(data)
        })
    }


    deleteUser = () => {
        fetch(`http://localhost:3000/api/v1/users/${this.props.user.id}`, {
    method: "DELETE"
    })
      }

    render() {
        let {email, phone, address, cityTown, state, zipCode, ccNum, expDate, securityCode, billingZip, open, dimmer} = this.state
        return (
            <center>
                <h1> {this.props.user.email}'s Profile</h1>
                <Grid textAlign='center' style={{ height: '1vh' }} verticalAlign='middle'>
                    <Grid.Column style={{ maxWidth: 1050 }}>
                        <Form size='small' onSubmit={() => this.handleProfileSubmit(this.state)}>
                            <Segment color='olive' >
                                <Form.Group>
                                    <Form.Input disabled label='First name' placeholder='First Name' width={6} />
                                    <Form.Input disabled label='Last Name' placeholder='Last Name' width={6} />
                                    <Form.Input name="email" value={email} label='E-mail' placeholder={this.props.user.email} width={4} />
                                    <Form.Input name="phone" value={phone} label='Phone' placeholder={this.props.user.phone} width={4} />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Input name="address" value={address} label='Address' placeholder={this.props.user.address} width={12} />
                                    <Form.Input name="cityTown" value={cityTown} label='City/Town' placeholder={this.props.user.city_town} width={6} />
                                    <Form.Input name="state" value={state} label='State' placeholder={this.props.user.state} width={2} />
                                    <Form.Input name="zipCode" value={zipCode} label='Zip Code' placeholder={this.props.user.zip_code} width={3} />
                                </Form.Group>
                                <Form.Group>
                                    <Form.Input name="ccNum" value={ccNum} label='Card Number' placeholder={this.props.user.cc_num} width={8} />
                                    <Form.Input name="expDate" value={expDate} label='Expiration Date' placeholder={this.props.user.exp_date} width={6} />
                                    <Form.Input name="securityCode" value={securityCode} label='Security Code' placeholder={this.props.user.security_code} width={2} />
                                    <Form.Input name="billingZip" value={billingZip} label='Billing Zip Code' placeholder={this.props.user.billing_zip} width={3} />
                                </Form.Group>
                                <Button color='red'onClick={this.show('blurring')}>Delete Account</Button>
                                <Button color='olive'>Submit</Button>
                            </Segment>
                        </Form>
                    </Grid.Column>
                </Grid>
            </center>
        )
    }

}
