import React from 'react'
import LoginForm from './LoginForm'
import Homepage from './Homepage'

export default class Content extends React.Component {

    renderContent = () => {
        if (this.props.loggedIn === true) {
          return <Homepage/>;
        } 
        // else if (this.props.viewer === true) {
        //   return <NoteViewer renderContent={this.renderContent} viewNote={this.props.viewNote} renderEditor={this.props.renderEditor}/>;
        // }
         else {
          return <LoginForm />;
        }
      }

    render() {
        return (
            <div>
                {this.renderContent()}
            </div>
        )
    }
}
