import React from 'react'
import { Button, Divider, Image, Container, Segment, Responsive, Visibility} from 'semantic-ui-react'
import ReactRotatingText from 'react-rotating-text'
import { NavLink } from 'react-router-dom'
import iunnologoblack from '../Assets/iunnologoblack.png'
import dinner from '../Assets/dinner.jpg'
import '/Users/ravenrosa/Desktop/Flatiron School/Mod5/iunno/frontend/src/CustomColors.css'



export default class Welcome extends React.Component {

    // getWidth = () => {
    //     const isSSR = typeof window === 'undefined'

    //     return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
    //   }

      renderWelcomeButtons = () => {
        if (this.props.user ==={}) {
          return <NavLink to='/homepage'><Button>Make an Order</Button></NavLink>
        }  else {
          return this.logRegButtons 
        }
      }

      logRegButtons = <Button.Group size='large'>
      <NavLink to="/login"><Button className='ui color1' content='Login' /></NavLink>
          <Button.Or />
      <NavLink to="/register"><Button className='ui color2' content='Register' /></NavLink>
      </Button.Group>

    render() {

        return (
            // <Responsive getWidth={this.getWidth} minWidth={Responsive.onlyTablet.minWidth}>
            //     <Visibility once={false} onBottomPassed={this.showFixedMenu} onBottomPassedReverse={this.hideFixedMenu}>
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                    <Segment vertical > 
                    {/* style={{ backgroundImage:`url(${dinner})`, height: '100%', padding: '1em 0em'} } */}
                        <center>
                            <Image src={iunnologoblack} alt='logo'/>
                            <h1>
                                <Divider hidden/>
                                <ReactRotatingText items={["IUNNO", "IDK", "I don't know",  "Yo no sé", "知りません", "Je ne sais pas", "Ich weiß es nicht", "我不知道", "انا لا اعرف"]} />
                            </h1>
                            <Divider hidden/>
                            {this.renderWelcomeButtons()}

                        </center>

                    </Segment>
                    </>
            //     </Visibility>
            // </Responsive>
        )
    }

}
