import React from 'react'
import { Form, Input, Divider, Message } from 'semantic-ui-react'
import Quantity from './Quantity'
import OrderButton from './OrderButton'
import Confirmation from './Confirmation'

export default class Homepage extends React.Component {

    state = {
        sessionQty: 0,
        orderCreated: false
    }

    addQty = () => {
        this.setState({sessionQty: 1})
    }

    chooseMeal = () => {
        this.props.allMeals[Math.floor(Math.random()*allMeals.length)]
    }

    createOrder = () => {

        fetch(`http://localhost:3000/orders}`, {
            method: 'POST',
            body: JSON.stringify({
              user_id: this.props.sessionUser.id, 
              meal_id: this.props.randomMeal.id


            }),
            headers:{
              'Content-Type': 'application/json',
              "Accept": "application/json"
            }
          })
          .then(res => res.json())
          .then(data => console.log(data))
          .then(this.setState({orderCreated: true}))
    }

    renderOrderContent = () => {
        if (this.state.sessionQty > 0) {
          return <OrderButton key={'orderbutton'} sessionQty={this.state.sessionQty} createOrder={this.createOrder}/>
        } else if (this.state.orderCreated === true) {
          return <Confirmation/>
        } else {
          return <Quantity key={'quantity'} addQty={this.addQty}/>;
        }
      }

    render() {
        console.log(this.state.sessionQty);

        return (
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                {this.renderOrderContent()}
          </>

        )
    }

}
