import React from 'react'
import { Form, Input, Divider } from 'semantic-ui-react'

export default class Homepage extends React.Component {

    state = {
        sessionQty:0
    }

    qtyForm = <Form onSubmit={this.props.addQty()} size='massive'>
                    <Form.Field inline>
                        <label>How Many People Are Eating?</label>
                        <Input placeholder='1+' />
                    </Form.Field>
                </Form>

    addQty = (evt) => {
        this.setState({sessionQty: evt.target.value}),
        this.qtyForm.remove
    }

    render() {
        console.log(this.state.sessionQty);

        return (
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                {this.qtyForm}
          </>

        )
    }

}
