import React from 'react'
import { Button, Divider, Image, Segment} from 'semantic-ui-react'
import ReactRotatingText from 'react-rotating-text'
import { NavLink } from 'react-router-dom'
import * as THREE from 'three'
import topology from '/Users/ravenrosa/Desktop/Flatiron School/Mod5/iunno/node_modules/vanta/dist/vanta.topology.min.js'
import iunnologoorange from '../Assets/iunnologoorange.png'
// import dinner from '../Assets/dinner.jpg'
import '/Users/ravenrosa/Desktop/Flatiron School/Mod5/iunno/frontend/src/CustomColors.css'



export default class Welcome extends React.Component {

  constructor() {
    super()
    this.vantaRef = React.createRef()
  }
  componentDidMount() {
    this.vantaEffect = topology({
      el: this.vantaRef.current,
      THREE: THREE
    })
  }
  componentWillUnmount() {
    if (this.vantaEffect) this.vantaEffect.destroy()
  }
      renderWelcomeButtons = () => {
        if (this.props.user === !{}) {
          return <NavLink to='/homepage'><Button>Make an Order</Button></NavLink>
        }  else {
          return this.logRegButtons 
        }
      }

      logRegButtons = <Button.Group size='large'>
      <NavLink to="/login"><Button className='ui color1' content='Login' /></NavLink>
          <Button.Or />
      <NavLink to="/register"><Button className='ui color2' content='Register' /></NavLink>
      </Button.Group>

    render() {

        return (
            <div ref={this.vantaRef}>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                    <Segment vertical > 
                    {/* style={{ backgroundImage:`url(${dinner})`, height: '100%', padding: '1em 0em'} } */}
                        <center>
                            <Image src={iunnologoorange} alt='logo'/>
                            <h1>
                                <Divider hidden/>
                                <ReactRotatingText items={["IUNNO", "IDK", "I don't know",  "Yo no sé", "知りません", "Je ne sais pas", "Ich weiß es nicht", "我不知道", "انا لا اعرف"]} />
                            </h1>
                            <Divider hidden/>
                            {this.renderWelcomeButtons()}
                        </center>
                    </Segment>
                    </div>
        )
    }

}
