import React from 'react'
import iunnologo4button from '../Assets/iunnologo4button.png'
import { Image } from 'semantic-ui-react';

export default class OrderButton extends React.Component {

    show = (dimmer) => () => this.setState({ dimmer, open: true })
    close = () => this.setState({ open: false })

    render() {
        const { open, dimmer } = this.state

        return (
        <div class="bigbutton-wrap">
            <button class="big-button" onClick={()=>{this.props.createOrder(); this.props.increment()}}><Image centered size='medium' src={iunnologo4button}/></button>
        </div>
        )
    }

}
