import React from 'react'
import { Form, Input, Divider, Message } from 'semantic-ui-react'
import Quantity from './Quantity'


export default class Homepage extends React.Component {

    state = {
        sessionQty:0
    }


    addQty = (evt) => {
        evt.preventDefault()
        this.setState({sessionQty: evt.target.value})

    }

    renderOrderContent = () => {
        if (this.props.editor === true) {
          return <NoteEditor allNotes={this.props.allNotes} viewNote={this.props.viewNote} renderNotes={this.props.renderNotes} renderView={this.props.renderView}/>;
        } else if (this.props.viewer === true) {
          return <NoteViewer renderContent={this.renderContent} viewNote={this.props.viewNote} renderEditor={this.props.renderEditor}/>;
        } else {
          return <Instructions />;
        }
      }

    render() {
        console.log(this.state.sessionQty);

        return (
            <>
                <Divider hidden/>
                <Divider hidden/>
                <Divider hidden/>
                {this.renderOrderContent}
                <Quantity key={'quantity'} addQty={this.addQty}/>
          </>

        )
    }

}
