import React from 'react';
import './App.css';
import MainContainer from './MainContainer'
import Header from './Header'
import {Switch, Route} from 'react-router'
import {withRouter} from 'react-router-dom'
import Forms from './Forms'
import ProfileContainer from './ProfileContainer'

// import dinner from './dinner.jpg'

class App extends React.Component {

  state = {
    user: {
      snacks: [],
      username: "",
      id: 0
    },
    token: ""
  }

  componentDidMount() {
    if (localStorage.getItem("token")) {
      let token = localStorage.getItem("token")

      fetch("http://localhost:3000/persist", {
        headers: {
          "Authorization": `bearer ${token}`
        }
      })
      .then(r => r.json())
      .then((data) => {
        if (data.token) {
          localStorage.setItem("token", data.token)
          this.setState({
            user: data.user,
            token: data.token
          }, () => {
            this.props.history.push("/profile")
          })
        }
      })


    }
  }

  addOneSnack = (snackObj) => {
    let modifiedArray = [...this.state.user.snacks, snackObj]
    this.setState({
      user: {
        ...this.state.user,
        snacks: modifiedArray
      }
    })
  }

  handleLoginSubmit = (userInfo) => {
    console.log("Login form has been submitted")
    fetch("http://localhost:3000/login", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      console.log(data);
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/profile")
        })

      }
    })

  }

  handleRegisterSubmit = (userInfo) => {
    fetch("http://localhost:3000/api/v1/users", {
      method: "POST",
      headers: {
        "content-type": "application/json"
      },
      body: JSON.stringify(
        userInfo
      )
    })
    .then(r => r.json())
    .then(data => {
      if (!data.error) {
        localStorage.setItem("token", data.token)
        this.setState({
          user: data.user,
          token: data.token
        }, () => {
          this.props.history.push("/profile")
        })
      }
    })
  }

  renderForms = (routerProps) => {
    if(routerProps.location.pathname === "/login"){
      return <Forms formName="Login Form" handleSubmit={this.handleLoginSubmit}/>
    } else if (routerProps.location.pathname === "/register") {
      return <Forms formName="Register Form" handleSubmit={this.handleRegisterSubmit}/>
    }
  }

  renderProfile = (routerProps) => {
    return <ProfileContainer token={this.state.token} user={this.state.user} addOneSnack={this.addOneSnack} />
  }

  render(){
    console.log(this.state, "APP STATE")
    console.log(this.props, "APP PROPS")

    return (
      <div className="App">
        <Header/>
        <Switch>
          <Route path="/login" render={ this.renderForm } />
          <Route path="/register" render={ this.renderForm } />
          <Route path="/profile" render={ this.renderProfile } />
          <Route path="/" exact render={() => <MainContainer handleRegisterSubmit={this.handleRegisterSubmit} handleLoginSubmit={this.handleLoginSubmit}/> } />
          <Route render={ () => <p>Page not Found</p> } />
        </Switch>
      </div>
    );
  }

}

export default withRouter(App);
